mapper.insert:
INSERT INTO USER_INFO
	(
		user_id
		, user_pw
		, user_name
	) VALUES (
		#{userId}
		, #{userPw}
		, #{userName}
	)

mapper.select:
SELECT  user_id
FROM 	user_info
WHERE 	user_id = #{userId}

SELECT  user_id
		, user_name
		, user_auth
FROM 	user_info
WHERE 	user_id = #{userId}
AND 	user_pw = #{userPw}
